    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>property</span> <span class="token attr-name">name</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>concatMap<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
